            this.testApple();
        }
    }
    async testApple() {
        const headers = { 'Authorization': 'Bearer ' + this.token };
        let something = await this.http.get('https://api.music.apple.com/v1/test', { headers }).ToPromise();
        console.log(something);
    }
    searchSong() {
        const headers = { 'Authorization': 'Bearer ' + this.token };
        this.playlists.forEach(song => {
            this.http.get('https://api.music.apple.com/v1/catalog/us/search?types=songs,albums,artists&limit=1' + '&term=' + song.title + '+' + song.artists[0], { headers }).subscribe(result => {
