        this.http.get(this.baseUrl + 'playlist').subscribe(result => {
            this.token = result;
            this.ConfigureMusicKit();
        }, error => console.error(error));
    }
    async onSubmit() {
        let linkValue = this.linkControl.value;
        const regex = '[0-9A-Za-z]+\\?';
        let found = linkValue.match(regex);
        let playlistId = found[0].slice(0, -1);
        let data = { Id: playlistId };
        const headers = { 'content-type': 'application/json' };
        await this.http.post(this.baseUrl + 'playlist', JSON.stringify(data), { headers }).subscribe(result => {
            this.playlists = result;
            console.log(result);
            this.searchSong();
            //this.music.authorize();
        }, error => console.error(error));
    }
}
