import { FormControl } from '@angular/forms';
import 'musickit-typescript';
import * as i0 from "@angular/core";
import * as i1 from "@angular/common/http";
import * as i2 from "@angular/forms";
export class ConverterComponent {
    constructor(http, baseUrl) {
        this.http = http;
        this.baseUrl = baseUrl;
        this.name = "";
        this.linkControl = new FormControl('');
        this.default = "https://open.spotify.com/playlist/5aJjvZYiPfszshEDxBcgpZ?si=23064bf926b149ab";
        document.addEventListener('musickitloaded', async function () {
            // Call configure() to configure an instance of MusicKit on the Web.
            try {
                await MusicKit.configure({
                    developerToken: 'DEVELOPER-TOKEN',
                    app: {
                        name: 'My Cool Web App',
                        build: '1978.4.1',
                    },
                });
            }
            catch (err) {
                // Handle configuration error
            }
            // MusicKit instance is available
            const music = MusicKit.getInstance();
        });
    }
    ;
    ngAfterViewInit() {
    }
    onSubmit() {
        let linkValue = this.linkControl.value;
        const regex = '[0-9A-Za-z]+\\?';
        let found = linkValue.match(regex);
        let playlistId = found[0].slice(0, -1);
        let data = { Id: playlistId };
        console.log(playlistId);
        const headers = { 'content-type': 'application/json' };
        this.http.post(this.baseUrl + 'playlist', JSON.stringify(data), { headers }).subscribe(result => {
            this.name = result;
        }, error => console.error(error));
    }
}
